#! /bin/bash
options="-std=c11 -O2"
warnings="-Waddress -Wall -Walloc-zero -Walloca -Warith-conversion -Warray-bounds=1 -Warray-parameter=2 -Wattribute-alias=2 -Wbool-compare -Wbool-operation -Wc++-compat -Wc++11-compat -Wc++14-compat -Wcast-align -Wcast-function-type -Wcatch-value -Wchar-subscripts -Wclobbered -Wcomment -Wcomments -Wdeprecated-copy -Wdouble-promotion -Wduplicate-decl-specifier -Wduplicated-branches -Wduplicated-cond -Wenum-compare -Wenum-conversion -Werror -Wexpansion-to-defined -Wextra -Wfloat-equal -Wformat -Wformat-security -Wformat-signedness -Wignored-qualifiers -Wimplicit -Wimplicit-fallthrough=3 -Wimplicit-function-declaration -Wimplicit-int -Wint-in-bool-context -Wjump-misses-init -Wlogical-not-parentheses -Wmain -Wmaybe-uninitialized -Wmemset-elt-size -Wmemset-transposed-args -Wmisleading-indentation -Wmissing-attributes -Wmissing-braces -Wmissing-field-initializers -Wmissing-parameter-type -Wmultistatement-macros -Wnarrowing -Wnonnull -Wnonnull-compare -Wnull-dereference -Wold-style-definition -Wparentheses -Wpedantic -Wpessimizing-move -Wpointer-arith -Wpointer-sign -Wrange-loop-construct -Wredundant-move -Wreorder -Wrestrict -Wreturn-type -Wsequence-point -Wshadow -Wshift-negative-value -Wsign-compare -Wsizeof-array-div -Wsizeof-pointer-div -Wsizeof-pointer-memaccess -Wstack-protector -Wstrict-aliasing -Wstrict-overflow=1 -Wstring-compare -Wstringop-overflow=2 -Wsuggest-attribute=[pure|const|noreturn|format|cold|malloc] -Wswitch -Wswitch-default -Wswitch-enum -Wtautological-compare -Wtraditional -Wtraditional-conversion -Wtrigraphs -Wtsan -Wtype-limits -Wundef -Wuninitialized -Wunsafe-loop-optimizations -Wunused -Wunused-but-set-variable -Wunused-local-typedefs -Wunused-macros -Wunused-variable -Wvla-parameter -Wvolatile-register-var"
gcc $options $warnings -iquote ./../include ./../source/*.c ./*.c